\define create-dum(max)
<$list filter="[range[1],{!!max}]" variable="_" counter="#">
<$action-createtiddler $basetitle={{{ $: Dum[<#>]+[join[/]]}}} tags="Dummy" text={{!!content}}/>
</$list>
\end

\define delete-dum()
<$action-deletetiddler $filter="[[Dummy]tagging[]]"/>
\end

;Number of tiddlers: <span class="input-autowidth" title="click and type to change the number"><$edit-text field="max" tooltip="click and type to change the number"/><span>{{!!max}}</span></span>

;Content of tiddlers:
<$edit-text tag=textarea field="content" class="text-editor"/>

<$button actions=<<create-dum max:{{!!max}}>>>
Create {{!!max}} dummy tiddlers
</$button>
<$button actions=<<delete-dum>>>
Delete <$count filter="[[Dummy]tagging[]]"/> dummy tiddlers
</$button>

<style>
.text-editor{
width:100%;
max-height:10em;
}

.input-autowidth{
box-sizing:border-box;
border-bottom:solid 1px;
min-width:2ch;
line-break: anywhere;
position:relative;
}

.input-autowidth :is(input,textarea){
all:unset;
position:absolute;
inset:0;
width:100%;
height:100%;
}

.input-autowidth span{
opacity:0;
pointer-events:none;
}

.input-autowidth span:empty:before{
content: '99';
}

.input-autowidth *{
font-family:inherit;
font-size:inherit;
}
</style>
